using System.Collections.Generic;
using System;

public class P
{
    public int sum { get; set; }
    public int p { get; set; }
}

public class hello
{
    public const int MOD = 1000000007;
    public static void Main()
    {
        var alp = "abcdefghijklmnopqrstuvwxyz";
        var d = new Dictionary<char, List<int>>();
        var d2 = new Dictionary<char, P>();
        for (int i = 0; i < 26; i++)
        {
            d[alp[i]] = new List<int>();
            d2[alp[i]] = new P { p = -1 };
        }
        var n = int.Parse(Console.ReadLine().Trim());
        var s = Console.ReadLine().Trim();
        var t = Console.ReadLine().Trim();
        for (int i = 0; i < n; i++) d[s[i]].Add(i);
        var ans = goDP(d, d2, s, t, n);
        Console.WriteLine(ans);
    }
    public static int goDP(Dictionary<char, List<int>> d, Dictionary<char, P> d2, string s, string t, int n)
    {
        var dp = new int[n];
        dp[0] = 1;
        for (int i = 1; i < n; i++)
        {
            dp[i] = getSum(d, d2, dp, t[i], i);
            d2[s[i]].p++;
            d2[s[i]].sum += dp[i];
            d2[s[i]].sum %= MOD;
        }
        return dp[n - 1];
    }
    public static int getSum(Dictionary<char, List<int>> d, Dictionary<char, P> d2, int[] dp, char c, int i)
    {
        var ans = 0;
        if (d2[c].p == -1 | d2[c].sum == 0)
        {
            var pp = 0;
            foreach (var x in d[c])
                if (x < i)
                {
                    ans += dp[x];
                    ans %= MOD;
                    pp++;
                }
                else break;
            if (pp > 0) pp--;
            d2[c].p = pp;
            d2[c].sum = ans;
        }
        else ans = d2[c].sum;
        return ans;
    }
}

