#include<complex>
using namespace std;
typedef complex<int> P;

int n, x, y;
int d[5001][5001];
P p[5000];

int square(int x) { return x * x; }

bool isOk(P p) { return d[p.real()][p.imag()] == 1; }

int main() {
  while (1) {
    scanf("%d", &n);
    if (n == 0) break;
    for (int i = 0; i <= 5000; i++) for (int j = 0; j <= 5000; j++) d[i][j] = 0;
    for (int i = 0; i < n; i++) {
      scanf("%d%d", &x, &y);
      d[y][x] = 1;
      p[i] = P(y, x);
    }
    int ans = 0;
    for (int i = 0; i < n; i++) for (int j = 0; j < i; j++) {
      P dir1 = p[i] - p[j];
      P dir2 = P(dir1.imag(), -dir1.real());
      for (int i = -1; i <= 1; i += 2) {
        P p1 = dir2 * i + p[j];
        P p2 = p1 + dir1;
        if (p1.real() < 0 || p1.imag() < 0 || p2.real() < 0 || p2.imag() < 0) continue;
        if (p1.real() >= 5001 || p1.imag() >= 5001 || p2.real() >= 5001 || p2.imag() >= 5001) continue;
        if (isOk(p1) && isOk(p2)) ans = max(ans, square(dir1.real()) + square(dir1.imag()));
      }
    }
    printf("%d\n", ans);
  }
}

