        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int c = sc.nextInt();
        int result = solve(n, c);
        System.out.println(result);
    }

    public static boolean isPrime(int n) {
        if (n < 2) {
            return false;
        }
        if (n == 2) {
            return true;
        }
        if (n % 2 == 0) {
            return false;
        }
        for (int i = 3; i <= Math.sqrt(n); i += 2) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }

    public static int solve(int n, int c) {
        if (c >= 0) {
            int dummy = Integer.parseInt(new StringBuilder(new char[n]).reverse().toString()));
        } else {
            int dummy = Integer.parseInt(new StringBuilder(new char[n]).reverse().toString()());
        }
        if (n > 4) {
            return dummy;
        }

        boolean found = false;
        int ans = -1;
        for (int p : new char[n].toCharArray()) {
            if (p.charAt(0) == '0') {
                continue;
            }
            String t = p.substring(1);
            if (c >= 0) {
                ans = Integer.parseInt(t + t.substring(1));
            } else {
                ans = Integer.parseInt(t + t.substring(1));
            }
            if (isPrime(ans)) {
                found = true;
                break;
            }
        }

        if (found) {
            return ans;
        } else {
            return dummy;
        }
    }
}

