                break;
            }

            double result = f(p / q, n, a, 1);

            System.out.println(result);
        }

        scanner.close();
    }

    public static double f(double x, double n, double a, int i) {
        if (x == 0) {
            return 0;
        }

        if (n == 0) {
            return 0;
        }

        if (i > a) {
            return x * (x / a);
        }

        double t = f(x, n, a, i + 1);

        while (true) {
            x -= 1 / i;
            n -= 1;
            a /= i;
            if (x < 0 || n < 0 || a < 1) {
                break;
            }
            t += f(x, n, a, i + 1);
        }

        return t;
    }
}

