    for(int i=0;i<n;i++){
        cin>>d[i];
        q[i].resize(d[i]);
        int t;
        cin>>t;
        for(int j=0;j<d[i];j++){
            cin>>q[i][(j+(d[i]-t))%d[i]];
        }
    }
    vector<vector<int>> gcdv(25,vector<int>(25,0));
    for(int i=0;i<25;i++){
        for(int j=0;j<25;j++){
            if(i!=0 && j!=0) gcdv[i][j]=__gcd(i,j);
        }
    }
    vector<vector<int>> tq(24);
    for(int i=0;i<24;i++){
        tq[i].assign(i+1,0);
    }
    for(int i=0;i<n;i++){
        for(int j=0;j<d[i];j++) tq[d[i]-1][j]+=q[i][j];
    }
    int cnt=0;
    auto calc=[&](vector<int> v){
        int res=0;
        for(int i=0;i<24;i++){
            int sc=-1e9;
            for(int j=0;j<i+1;j++){
                bool isok=true;
                for(int k=0;k<vec.size();k++){
                    int g=gcdv[vec[k]][i+1];
                    isok=isok && (v[k]%g==j%g);  
                    cnt++;
                }
                if(isok) sc=max(sc,tq[i][j]);
            }
            res+=sc;
        }
        return res;
    };
    function<int(int,vector<int>)> dfs=[&](int idx,vector<int> v){
        if(idx==vec.size()) return calc(v);
        int res=0;
        for(int i=0;i<vec[idx];i++){
            v[idx]=i;
            res=max(res,dfs(idx+1,v));
        }
        return res;
    };

    vector<int> tmp(vec.size());
    int ret=dfs(0,tmp);
    return ret;
}
int main(){
    int n;
    while(cin>>n,n){
        cout<<solve(n)<<endl;
    }
    return 0;
}

