        for (int i = 0; i < n; i++) {
            id[i] = i;
            size[i] = 1;
        }
    }

    static int root(int i) {
        while (i!= id[i]) {
            id[i] = id[id[i]];
            i = id[i];
        }
        return i;
    }

    public static boolean connected(int p, int q) {
        return root(p) == root(q);
