using namespace std;

/* constant */

const int MAX_N = 100000;

/* typedef */

typedef vector<int> vi;
typedef queue<int> qi;
typedef pair<int,int> pii;

struct Node {
  int v;
  Node *lpt, *rpt, *ppt;
  Node() {}

  void print() {
    printf("(%d:P%d,L%d,R%d)",
	   v, ppt ? ppt->v : -1, lpt ? lpt->v : -1, rpt ? rpt->v : -1);
  }
};

/* global variables */

int as[MAX_N];
Node buf[MAX_N];

/* subroutines */

Node *maketree(int i0, int i1) {
  if (i0 >= i1) return NULL;
  int im = (i0 + i1) / 2;
  Node *npt = buf + as[im];
  npt->v = as[im];
  npt->ppt = NULL;
  if ((npt->lpt = maketree(i0, im)) != NULL) npt->lpt->ppt = npt;
  if ((npt->rpt = maketree(im + 1, i1)) != NULL) npt->rpt->ppt = npt;
  return npt;
}

void traverse(vi &v, Node *npt) {
  if (npt->lpt != NULL) traverse(v, npt->lpt);
  v.push_back(npt->v);
  if (npt->rpt != NULL) traverse(v, npt->rpt);
}

void printtree(Node *npt) {
  vi v;
  traverse(v, npt);
  //printf("root=%d: ", npt->v);
 for (int i = 0; i < v.size(); i++) {
    if (i) putchar(' ');
    printf("%d", v[i] + 1);
    //buf[v[i]].print();
  }
  putchar('\n');
}

Node *rot_l(Node *npt0) {
  if (npt0 == NULL || npt0->rpt == NULL) return npt0;
  Node *npt1 = npt0->rpt;
  npt0->rpt = npt1->lpt;
  if (npt1->lpt) npt1->lpt->ppt = npt0;
  npt1->lpt = npt0;
  npt1->ppt = npt0->ppt; npt0->ppt = npt1;
  if (npt1->ppt) {
    if (npt1->ppt->lpt == npt0) npt1->ppt->lpt = npt1;
    else npt1->ppt->rpt = npt1;
  }
  return npt1;
}

Node *rot_r(Node *npt0) {
  if (npt0 == NULL || npt0->lpt == NULL) return npt0;
  Node *npt1 = npt0->lpt;
  npt0->lpt = npt1->rpt;
  if (npt1->rpt) npt1->rpt->ppt = npt0;
  npt1->rpt = npt0;
  npt1->ppt = npt0->ppt; npt0->ppt = npt1;
  if (npt1->ppt) {
    if (npt1->ppt->lpt == npt0) npt1->ppt->lpt = npt1;
    else npt1->ppt->rpt = npt1;
  }
  return npt1;
}

Node *maketop(Node *npt) {
  while (npt->ppt != NULL) {
    Node *npt0 = npt->ppt;
    if (npt0->lpt == npt) rot_r(npt0);
    else rot_l(npt0);
  }
  return npt;
}

/* main */

int main() {
  int n, qn;
  scanf("%d%d", &n, &qn);
  for (int i = 0; i < n; i++)
    scanf("%d", as + i), as[i]--;

  Node *root = maketree(0, n);
  //printtree(root);

  while (qn--) {
    int ai;
    scanf("%d", &ai);
    ai--;

    root = maketop(&buf[ai]);
    swap(root->lpt, root->rpt);
    //printtree(root);
  }

  printtree(root);
  return 0;
}

