            int x = Integer.parseInt(parts[0]);
            int y = Integer.parseInt(parts[1]);

            if (x == 0) return 0;

            int distance = Math.abs(x / 2 * 2);
            int distance2 = Math.abs(y / 2 * 2);

            if (distance < distance2) return distance2;

            if (distance == distance1) return distance2;

            System.out.println(x == 0? (x / 2 * 2) + (y / 2 * 2) + Math.max(x % 2, y % 2) : (y == 1)? y + y % 2 : 1);
        }
    }
}

