        visited = new boolean[n][n];
        dp = new int[n][n];
        dp[0][0] = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (points[i][j] == points[j][j]) {
                    dp[i][j] = 0;
                }
            }
        }
        System.out.println(calc(0, 0, 0));
    }

    public static int calc(int p1, int p2, int v) {
        int[] dists_v = dists[v];
        if (v == n - 1) {
            return dists_v[p1] + dists_v[p2];
        }
        if (visited[p1][p2]) {
            return dp[p1][p2];
        }
        if (dp[p1][p2]!= 0) {
            return dp[p1][p2];
        }
        dp[p1][p2] = Math.min(dists_v[p1] + calc(p2, v, v + 1), dists_v[p2] + calc(p1, v, v + 1));
        visited[p1][p2] = true;
        return dp[p1][p2];
    }
}

