int nextperm(int p[], int N)  /*  */
{
    int i, j, t;

    i = N - 1;
    p[0] = 0;  /*  */
    while (p[i] >= p[i + 1]) i--;
    if (i == 0) return 0;  /*  */
    j = N;
    while (p[i] >= p[j]) j--;
    t = p[i];  p[i] = p[j];  p[j] = t;
    i++;  j = N;
    while (i < j) {
        t = p[i];  p[i] = p[j];  p[j] = t;  i++;  j--;
    }
    return 1;  /*  */
}

