        static void Solve()
        {
            var HWN = Console.ReadLine().Split(' ').Select(x => int.Parse(x)).ToArray();
            H = HWN[0];
            W = HWN[1];
            N = HWN[2];
            var SP = Console.ReadLine().Split(' ').Select(x => int.Parse(x)).ToArray();
            SH = SP[0];
            SW = SP[1];
            S = Console.ReadLine();
            T = Console.ReadLine();
            var ans = false;
            if (IsOkWide() && IsOkHeight())
            {
                ans = true;
            }
            Console.WriteLine(ans ? "YES" : "NO");
        }

        static bool IsOkWide()
        {
            var min = 1;
            var max = W;
            for (int i = (int)N - 1; i >= 0; i--)
            {
                if(T[i] == 'L')
                {
                    max = Min(max + 1, W);
                }else if (T[i] == 'R')
                {
                    min = Max(min - 1, 1);
                }

                if (S[i] == 'L')
                {
                    min++;
                }else if (S[i] == 'R')
                {
                    max--;
                }

                if(min > max)
                {
                    return false;
                }
            }
            return (min <= SW) &&(SW <= max);
        }

        static bool IsOkHeight()
        {
            var min = 1;
            var max = H;
            for (int i = (int)N - 1; i >= 0; i--)
            {
                if (T[i] == 'U')
