                A[i] = (26+S[i]-S[S.Length-1-i])%26;
            }
            Q = re.i();
            List<int> LL = new List<int>();
            List<int> RR = new List<int>();
            for(int i=0;i<Q;i++){
                int l = re.i()-1;
                int r = re.i()-1;
                if(l*2 == n-1 && r*2 == n-1){

                }
                else if(r*2 == n-1){
                    LL.Add(l);
                    RR.Add(N-1);
                }
                else if(l*2 == n-1){
                    LL.Add(S.Length-1-r);
                    RR.Add(N-1);
                }
                else if(l*2 < n-1 && r*2 < n-1){
                    LL.Add(l);
                    RR.Add(r);
                }
                else if(l*2 < n-1 && r*2 > n-1){
                    if(r+l > n-1){
                        LL.Add(S.Length-1-r);
                        RR.Add(l-1);
                    }
                    if(n-1 > r+l){
                        LL.Add(l);
                        RR.Add(S.Length-1-r-1);
                    }
                }
                else{
                    LL.Add(S.Length-1-r);
                    RR.Add(S.Length-1-l);
                }
            }
            Q = LL.Count;
            L = LL.ToArray();
            R = RR.ToArray();
        }
        UnionFind U = new UnionFind(N+1);
        for(int i=0;i<Q;i++){
            U.Union(L[i],R[i]+1);
        }
        int[] B = new int[N+1];
        for(int i=0;i<N;i++){
            B[U.Get(i)] += A[i]+(i == 0 ? 0 : 26-A[i-1]);
        }
        for(int i=0;i<=N;i++){
            if(B[i] % 26 != 0 && !U.Same(i,N)){
                sb.Append("NO\n");
                return;
            }
        }
        sb.Append("YES\n");
    }
}
class UnionFind{
    int[] par;
    public UnionFind(int n){
        par = new int[n];
        for(int i=0;i<n;i++){
            par[i] = i;
        }
    }
    public void Union(int x,int y){
        par[Get(x)] = Get(y); 
    }
    public bool Same(int x,int y){
        return Get(x) == Get(y);
    }
    public int Get(int x){
        if(x != par[x]){
            par[x] = Get(par[x]);
        }
        return par[x];
    }
