	public class RangeFenwickTree : IEnumerable<long>
	{
		private readonly int n_;
		private readonly long[,] bit_;

		public RangeFenwickTree(int n)
		{
			n_ = n;
			bit_ = new long[2, n_ + 1];
		}

		public void Add(int l0, int r0, long value)
		{
			++l0;
			++r0;
			AddCore(0, l0, -value * (l0 - 1));
			AddCore(1, l0, value);
			AddCore(0, r0, value * (r0 - 1));
			AddCore(1, r0, -value);
		}

		private void AddCore(int p, int a1, long value)
		{
			for (int i = a1; i <= n_; i += i & -i) {
				bit_[p, i] += value;
			}
		}

		public long Sum(int i0)
		{
			++i0;
			return SumCore(0, i0) + SumCore(1, i0) * i0;
		}

		public long Sum(int l0, int r0) => Sum(r0 - 1) - Sum(l0 - 1);

		private long SumCore(int p, int a1)
		{
			long res = 0;
			for (int i = a1; i > 0; i -= i & -i) {
				res += bit_[p, i];
			}

			return res;
		}

		System.Collections.IEnumerator System.Collections.IEnumerable.GetEnumerator() => GetEnumerator();
		public IEnumerator<long> GetEnumerator()
		{
			for (int i = 0; i < n_; i++) {
				yield return Sum(i);
			}
		}
	};

	public struct ModInt
	{
		public const long P = 1000000007;
		//public const long P = 998244353;

		public static ModInt Inverse(ModInt value) => Pow(value, P - 2);
		public static ModInt Pow(ModInt value, long k) => Pow(value.value_, k);
		public static ModInt Pow(long value, long k)
		{
			long ret = 1;
			for (k %= P - 1; k > 0; k >>= 1, value = value * value % P) {
				if ((k & 1) == 1) {
					ret = ret * value % P;
				}
			}
			return new ModInt(ret);
		}

		private long value_;

		public ModInt(long value)
			=> value_ = value;
		public ModInt(long value, bool mods)
		{
			if (mods) {
				value %= P;
				if (value < 0) {
					value += P;
				}
			}

			value_ = value;
		}

		public static ModInt operator +(ModInt lhs, ModInt rhs)
		{
			lhs.value_ = (lhs.value_ + rhs.value_) % P;
			return lhs;
		}
		public static ModInt operator +(long lhs, ModInt rhs)
		{
			rhs.value_ = (lhs + rhs.value_) % P;
			return rhs;
		}
		public static ModInt operator +(ModInt lhs, long rhs)
		{
			lhs.value_ = (lhs.value_ + rhs) % P;
			return lhs;
		}

		public static ModInt operator -(ModInt lhs, ModInt rhs)
		{
			lhs.value_ = (P + lhs.value_ - rhs.value_) % P;
			return lhs;
		}
		public static ModInt operator -(long lhs, ModInt rhs)
		{
			rhs.value_ = (P + lhs - rhs.value_) % P;
			return rhs;
		}
		public static ModInt operator -(ModInt lhs, long rhs)
		{
			lhs.value_ = (P + lhs.value_ - rhs) % P;
			return lhs;
		}

		public static ModInt operator *(ModInt lhs, ModInt rhs)
		{
			lhs.value_ = lhs.value_ * rhs.value_ % P;
			return lhs;
		}
		public static ModInt operator *(long lhs, ModInt rhs)
		{
			rhs.value_ = lhs * rhs.value_ % P;
			return rhs;
		}
		public static ModInt operator *(ModInt lhs, long rhs)
		{
			lhs.value_ = lhs.value_ * rhs % P;
			return lhs;
		}

		public static ModInt operator /(ModInt lhs, ModInt rhs)
		{
			long exp = P - 2;
			while (exp > 0) {
				if (exp % 2 > 0) {
					lhs *= rhs;
				}

				rhs *= rhs;
				exp /= 2;
			}

			return lhs;
		}

		public static implicit operator ModInt(long n) => new ModInt(n, true);
		public long ToLong() => value_;
		public override string ToString() => value_.ToString();
	}

