    static int n, k;
    static int M = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        k = sc.nextInt();
        sc.close();

        int[] ds = new int[n];
        for (int i = 0; i < n; i++) {
            ds[i] = sc.nextInt();
        }

        int m = ds.length;
        int[] nums = new int[m];
        for (int i = 0; i < m; i++) {
            int val = pow(k, (ds[i] + 1) / 2, M);
            for (int j = 0; j < i; j++) {
                if (ds[i] % ds[j] == 0) {
                    val -= nums[j];
                    val %= M;
                }
            }
            nums[i] = val % M;
        }

        int ans = 0;
        int inv2 = Math.pow(2, M - 2, M);
        for (int i = 0; i < m; i++) {
            if (ds[i] % 2 == 0) {
                ans += ds[i] * nums[i] * inv2;
            } else {
                ans += ds[i] * nums[i];
            }
        }
        ans %= M;
        System.out.println(ans % M);
    }

    public static int pow(int a, int b) {
        if (b == 0) {
            return a;
        } else {
            return a * b;
        }
    }
}

