import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
        int K = sc.nextInt();
        int[] A = new int[N];
        for (int i = 0; i < N; i++) {
            A[i] = sc.nextInt();
        }

        Map<Integer, List<Integer>> rtree = new HashMap<>();
        for (int i = 1; i <= N; i++) {
            rtree.put(A[i - 1], new ArrayList<>());
        }

        for (int i = 2; i <= N; i++) {
            int m = A[i - 1];
            rtree.get(m).add(i);
        }

        String nn = new String(Arrays.toString(A));
        for (int i = 2; i <= N; i++) {
            nn.add(A[i - 1]);
        }

        String nodes = sc.nextLine();
        int[] dp = new int[N + 1];
        int ans = A[0] == 1? 0 : 1;

        for (int i = 1; i <= N; i++) {
            int n = nodes[i];
            dp[n] = (rtree.get(n).stream().mapToInt(i -> dp[i])).max().getAsInt() + 1;
            if (A[n - 1] == 1) {
                dp[n] = 0;
            } else if (dp[n] == K) {
                dp[n] = 0;
                ans++;
            }
        }

        System.out.println(ans);
    }
}

