#include <stdio.h>


int trsr_v[1001], trsr_w[1001], dp[1001][1001];


int main(void) {
    int W, N, v, w, i, case_id = 0;

    while (scanf("%d", &W) == 1 && W) {
        case_id++;
        scanf("%d", &N);
        for (i = 1; i <= N; i++) {
            scanf("%d,%d", &trsr_v[i], &trsr_w[i]);
        }

        for (i = 0; i <= N; i++) {
            for (w = 0; w <= W; w++) {
                dp[i][w] = w == 0? 0: -1;
            }
        }
        for (i = 1; i <= N; i++) {
            for (w = 1; w <= W; w++) {
                if (w < trsr_w[i]) {
                    dp[i][w] = dp[i-1][w];
                }
                else {
                    if (dp[i-1][w-trsr_w[i]] >= 0) {
                        v = dp[i-1][w-trsr_w[i]] + trsr_v[i];
                        dp[i][w] = v > dp[i-1][w]? v: dp[i-1][w];
                    }
                    else {
                        dp[i][w] = dp[i-1][w];
                    }
                }
            }
        }
        for (w = W; w > 0; w--) {
            if (dp[N][w] < 0) continue;
            if (dp[N][w] > dp[N][w-1]) {
                break;
            }
        }
        printf("Case %d:\n%d\n%d\n", case_id, dp[N][w], w);
    }
    return 0;
}

