using System.Linq;

class Program {
    public IDictionary<string, int> Items { get { return items; } }
    public IDictionary<string, IEnumerable<string>> Recipes { get { return recipes; } }

    Dictionary<string, int> items = new Dictionary<string, int>();
    Dictionary<string, IEnumerable<string>> recipes = new Dictionary<string, IEnumerable<string>>();

    static void Main() {
        for (string line; (line = Console.ReadLine()) != "0";) {
            var calc = new Program();

            var n = int.Parse(line);
            for (var i = 0; i < n; i++) {
                var l = Console.ReadLine().Split(' ');
                calc.Items.Add(l[0], int.Parse(l[1]));
            }

            n = int.Parse(Console.ReadLine());
            for (int i = 0; i < n; i++) {
                var l = Console.ReadLine().Split(' ');
                var name = l[0];
                var materials = l.Skip(2);
                calc.Recipes.Add(name, materials);
            }

            var item = Console.ReadLine();
            Console.WriteLine(calc.Calculate(item));
        }
    }

    public int Calculate(string item) {
        IEnumerable<string> materials;
        if (recipes.TryGetValue(item, out materials)) {
            var totalCost = 0;

            foreach (var material in materials) {
                var cost = Calculate(material);
                totalCost += cost;
            }

            return Math.Min(items[item], totalCost);
        }

        return items[item];
    }
}

