pair<int, int> solve(int H, int W, int N, vector<vector<int>> f) {

    vector<vector<int>> dp(H + 1, vector<int>(W + 1, 0));
    dp[0][0] = N - 1;
    FOR(y, 0, H) {
        FOR(x, 0, W) {
            int num = dp[y][x];
            if (num % 2 == 0) {
                dp[y + 1][x] += num / 2;
                dp[y][x + 1] += num / 2;
            }
            else {
                if (f[y][x] == 0) {
                    dp[y + 1][x] += num - (num / 2);
                    dp[y][x + 1] += num / 2;
                }
                if (f[y][x] == 1) {
                    dp[y + 1][x] += num / 2;
                    dp[y][x + 1] += num - (num / 2);
                }
            }
        }
    }

    FOR(y, 0, H) {
        FOR(x, 0, W) {
            if (dp[y][x] % 2 != 0) {
                f[y][x] = 1 - f[y][x];
            }
        }
    }

    int y = 0, x = 0;
    while (true) {
        int p = f[y][x];
        if (p == 0) {
            y += 1;
        }
        else {
            x += 1;
        }

        if (y == H or x == W) {
            break;
        }
    }

    return make_pair(y, x);
}

