const int N = 20001;
vector<int> factor[N];
bool p[N];

Long combi(int n, int r) {
  map<int, int> mp;
  REP(i,r) {
    FOR(it, factor[n-r+1+i]) mp[*it]++;
  }
  REP(i,r) {
    FOR(it, factor[r-i]) mp[*it]--;
  }
  Long res = 1;
  FOR(it, mp) {
    REP(i,it->second) res *= it->first;
  }
  return res;
}

