        for (int i = 0; i < N; i++) {
            ans += L[i];
        }
        System.out.println(ans);
    }

    public static void dfs(int i, int mi, int ma) {
        if (i == N) {
            ans = Math.min(ans, ma - mi);
            return;
        }
        int key = (mi, ma);
        if (ma - mi >= ans || memo[i].contains(key)) {
            return;
        }
        memo[i].add(key);
        int s = 0;
        for (int j = i; j < N - (i == 0); j++) {
            s += L[j];
            dfs(j + 1, Math.min(mi, s), Math.max(ma, s));
        }
    }
}

