        for (int[] l : lists) for (int d : l) numOfCommittee[d]++;
        int max = numOfCommittee.length;
        if (max < Q) return 0;
        int index = Arrays.binarySearch(numOfCommittee, max);
        if (index < 0) return -index;
        int maxIndex = Arrays.binarySearch(numOfCommittee, max);
        return maxIndex < Q? -maxIndex : Arrays.binarySearch(numOfCommittee, max);
    }
}

