            s = s.Replace(",b", "b");
            getAns(s);
        }
    }
    static void getAns(string s)
    {
        while (true)
        {
            var ma = Regex.Matches(s, @"a[0-9,]+b");
            if (ma.Count == 0) break;
            foreach (Match x in ma)
            {
                var a0 = x.Value.Substring(1, x.Length - 2).Split(',');
                var a1 = new List<int>();
                for (int i = 0; i < a0.Length; i++) a1.Add(int.Parse(a0[i]));
