        var t2 = getDiv(b);
        var ans = int.MaxValue;
        for (int i = 0; i < t1.Count(); i++)
            for (int j = 0; j < t2.Count(); j++)
            {
                var w = getSum(t1[i], t2[j]);
                ans = Min(ans, w);
            }
        Console.WriteLine(ans);
    }
    static int getSum(P p1, P p2)
    {
        var a = new int[4];
        a[0] = p1.x; a[1] = p1.y; a[2] = p2.x; a[3] = p2.y;
        Array.Sort(a);
        return (a[0] - a[1]) * (a[0] - a[1]) + (a[1] - a[2]) * (a[1] - a[2]) +
            (a[2] - a[3]) * (a[2] - a[3]);
    }
    static List<P> getDiv(int n)
    {
        var ans = new List<P>();
        for (int i = 1; i * i <= n; i++)
            if (n % i == 0) ans.Add(new P { x = i, y = n / i });
        return ans;
    }
}

