void cw(P &p){
  P n;
  for(int i=0;i<3;i++){
    for(int j=0;j<3;j++){
      n[j*3+2-i]=p[i*3+j];
    }
  }
  p=n;
}

void ccw(P &p){
  cw(p);
  cw(p);
  cw(p);
}

