        need[b].add(Pair(a, c))
    }
    println(solve(need))
}
fun solve(needs: Array<out Collection<Pair<Int, Int>>>): Int {
    val memo = IntArray(needs.size){-1}
    memo[0] = 0
    fun sub(current: Int): Int {
        return when(memo[current]){
            -1 -> {
                memo[current] = needs[current].map{(a, c) -> sub(a) + c}.max()!!
                memo[current]
            }
            else -> memo[current]
        }
    }
    return sub(needs.size - 1)
}

