    public static int[] knapsack(int[][] items, int budget) {
        int[][] table = new int[budget + 1][budget + 1];
        for (int i = 0; i < items.length; i++) {
            int c = items[i][0];
            int v = items[i][1];
            for (int j = 0; j < budget; j++) {
                int x = table[0][j];
                int y = (j >= c? table[0][j - c] + v : 0);
                table[1][j] = x > y? x : y;
            }
            table = new int[table.length][budget + 1];
        }
        table[0][budget] = table[0][budget];
        return table[0][budget];
    }
}

