namespace Solver
{

    public class Solver
    {

        public void Solve()
        {
            while (true)
            {
                var n = sc.Integer();
                if (n == 0) break;
                var s = Enumerate(n, x => sc.Scan().ToCharArray());
                for (int k = 1; k < n; k++)
                {
                    int i = 0;
                    for (; i < s[k].Length && s[k][i] == '.'; i++)
                    {
                        if (s[k][i] == '.') s[k][i] = ' ';
                    }
                    s[k][i - 1] = '+';
                    var f = false;
                    for (int j = k - 1; j >= 0; j--)
                    {
                        if (s[j][i - 1] == ' ') continue;
                        if (s[j][i - 1] == '+') f = true;
                        break;
                    }
                    if (!f) continue;
                    for (int j = k - 1; j >= 0; j--)
                    {
                        if (s[j][i - 1] == ' ')
                        {
                            s[j][i - 1] = '|';
                            continue;
                        }
                        break;
                    }

                }
                foreach(var t in s)
                    IO.Printer.Out.WriteLine(t.AsString());
            }
        }


        internal IO.StreamScanner sc;
        static T[] Enumerate<T>(int n, Func<int, T> f) { var a = new T[n]; for (int i = 0; i < n; i++) a[i] = f(i); return a; }
    }

    #region Main and Settings
