            ret[i] = llround(i & 1 ? fa[i >> 1].y : fa[i >> 1].x);
        }
        return ret;
    }
};

template< typename T >
struct ArbitraryModConvolution {
    using real = FastFourierTransform::real;
    using C = FastFourierTransform::C;

    ArbitraryModConvolution() = default;

    vector< T > multiply(const vector< T > &a, const vector< T > &b, int need = -1) {
        if(need == -1) need = a.size() + b.size() - 1;
        int nbase = 0;
        while((1 << nbase) < need) nbase++;
        FastFourierTransform::ensure_base(nbase);
        int sz = 1 << nbase;
        vector< C > fa(sz);
        for(int i = 0; i < a.size(); i++) {
            fa[i] = C(a[i].x & ((1 << 15) - 1), a[i].x >> 15);
        }
        fft(fa, sz);
        vector< C > fb(sz);
        if(a == b) {
            fb = fa;
        }else{
            for(int i = 0; i < b.size(); i++) {
                fb[i] = C(b[i].x & ((1 << 15) - 1), b[i].x >> 15);
            }
            fft(fb, sz);
        }
        real ratio = 0.25 / sz;
        C r2(0, -1), r3(ratio, 0), r4(0, -ratio), r5(0, 1);
        for(int i = 0; i <= (sz >> 1); i++) {
            int j = (sz - i) & (sz - 1);
            C a1 = (fa[i] + fa[j].conj());
            C a2 = (fa[i] - fa[j].conj()) * r2;
            C b1 = (fb[i] + fb[j].conj()) * r3;
            C b2 = (fb[i] - fb[j].conj()) * r4;
            if(i != j) {
                C c1 = (fa[j] + fa[i].conj());
                C c2 = (fa[j] - fa[i].conj()) * r2;
                C d1 = (fb[j] + fb[i].conj()) * r3;
                C d2 = (fb[j] - fb[i].conj()) * r4;
                fa[i] = c1 * d1 + c2 * d2 * r5;
                fb[i] = c1 * d2 + c2 * d1;
            }
            fa[j] = a1 * b1 + a2 * b2 * r5;
            fb[j] = a1 * b2 + a2 * b1;
        }
        fft(fa, sz);
        fft(fb, sz);
        vector< T > ret(need);
        for(int i = 0; i < need; i++) {
            int64_t aa = llround(fa[i].x);
            int64_t bb = llround(fb[i].x);
            int64_t cc = llround(fa[i].y);
            aa = T(aa).x, bb = T(bb).x, cc = T(cc).x;
            ret[i] = aa + (bb << 15) + (cc << 30);
        }
        return ret;
    }
};


int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);
    string S;
    cin >> S;
    int N = S.size();
    int cnt = 0;
    for(int i=0;i<N;i++) cnt += S[i]=='?';
    ArbitraryModConvolution<mint> AMC;
    mint three = 3;
    mint inv3 = three.inverse();
    //[l,m),[m,r)
    auto calc = [&](int l,int m,int r)->mint{
        if(r-l<3) return 0;
        int n = r-l;
        vec<mint> U(n),G(n);
        for(int i=l;i<m;i++){
            if(S[i]=='U') U[i-l] = 1;
            if(S[i]=='?') U[i-l] = inv3;
        }
        for(int i=m;i<r;i++){
            if(S[i]=='G') G[i-l] = 1;
            if(S[i]=='?') G[i-l] = inv3;
        }
        auto R = AMC.multiply(U,G);
        mint res = 0;
        for(int i=l;i<r;i++){
            if(S[i]=='M') res += R[2*(i-l)];
            if(S[i]=='?') res += R[2*(i-l)]*inv3;
        }
        return res;
    };

    auto divide = [&](auto&& self,int l,int r)->mint{
        if(r-l<3) return 0;
        mint res = 0;
        int m = (l+r)/2;
        res += self(self,l,m)+self(self,m,r);
        res += calc(l,m,r);
        return res;
    };

    mint ans = divide(divide,0,N)*three.pow(cnt);
    cout << ans << "\n";
}

