  for(int i = 0;i < n;i++){
    for(int j = 0;j < n;j++){
      if(queen[i][j] && row[i] != j) return ;
    }
  }

  for(int i = 0;i < n;i++){
    for(int j = 0;j < n;j++){
      if(row[i] == j) cout << 'Q';
      else cout << '.';
    }
    cout << endl;
  }
}

