  if(i == n){
    printBoard();
    return ;
  }

  for(int j = 0;j < n;j++){
    if(col[j] || dpos[i + j] || dneg[i - j + n - 1]) continue;

    row[i] = j;
    col[j] = dpos[i + j] = dneg[i - j + n - 1] = 1; //not free

    putQueen(i + 1);

    col[j] = dpos[i + j] = dneg[i - j + n - 1] = 0; //free
  }
}

