class Program
{

    static bool Prime (int max)
    {
        bool jud = true;

        for (int i = 2; i <= Math.Floor(Math.Sqrt(max)); i++)
        {
            if (max % i == 0)
            {
                jud = false;
                break;
            }
        }
        return jud;
    }

