        selectionSort(s, n);
        System.out.println("Not stable");
    }

    public static void bubbleSort(int[] a, int n) {
        for (int i = 0; i < n - 2; i++) {
            for (int j = 0; j < n - 1; j++) {
                if (a[j] < a[j - 1]) {
                    int temp = a[j];
                    a[j] = a[j - 1];
                    a[j - 1] = temp;
                }
            }
        }
    }

    public static void selectionSort(int[] a, int n) {
        for (int i = 0; i < n; i++) {
            int m = i;
            for (int j = i; j < n - 1; j++) {
                if (a[j] < a[m]) {
                    m = j;
                }
            }
            int temp = a[i];
            a[i] = a[m];
            a[m] = temp;
        }
    }
}

