public class Queue<T>{
    private T[] Q;
    private int head;
    private int tail;
    private int MAX;

    public Queue(int n){
        Q = new T[n];
        MAX = n;
    }

    public void Initialize(){
        head = tail = 0;
    }

    public bool IsEmpty(){
        return head == tail;
    }

    public bool IsFull(){
        return head == (tail+1) % MAX;
    }

    public void Enqueue(T x){
        if(IsFull()){
            Console.WriteLine("error");
        }
        Q[tail] = x;
        tail = (tail+1) % MAX;
    }

    public T Dequeue(){
        if(IsEmpty()){
            Console.WriteLine("error");
        }
        T x = Q[head];
        head = (head+1) % MAX;

        return x;
    }

    public void Print(){
        while(!IsEmpty()){
            Console.WriteLine(this.Dequeue().ToString());
        }
    }
}

