class UF:
    """Union Find
    """
    def __init__(self, n):
        self.n = n
        self.elements = [i for i in range(n)]
        self.size = [1 for _ in range(n)]

    def union(self, v, w):
        assert 0 <= v < self.n
        assert 0 <= w < self.n
        rv = self.root(v)
        rw = self.root(w)

        if rv != rw:
            if self.size[rv] > self.size[rw]:
                self.elements[rw] = rv
                self.size[rv] += self.size[rw]
            else:
