            int V = int.Parse(input[0]);
            int E = V - 1;

            Dictionary<int, List<Edge>> G = new Dictionary<int, List<Edge>>();
            for (int i = 0; i < V; i++)
            {
                G.Add(i, new List<Edge>());
            }

            for (int i = 0; i < E; i++)
            {
                input = Console.ReadLine().Split(' ');
                int s = int.Parse(input[0]);
                int t = int.Parse(input[1]);
                int w = int.Parse(input[2]);

                G[s].Add(new Edge(t, w, -1));
                G[t].Add(new Edge(s, w, -1));
            }

            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < V; i++)
            {
                int temp = DFS(G, -1, i);
                sb.AppendLine(temp.ToString());
            }
            Console.Write(sb);
        }

        static int DFS(Dictionary<int, List<Edge>> G, int prev, int cur)
        {
            int ret = 0;

            foreach (var e in G[cur])
            {
                int next = e.to;
                if (next == prev) continue;
                if (e.height == -1) e.height = e.distance + DFS(G, cur, next);
                ret = Math.Max(ret, e.height);
            }
            return ret;
        }
    }
