    }

    private static String ntl_1_a(long n) {
        long na = n;

        List<Pair<Long, Long>> pairList = new ArrayList<>();
        int sqrt = Math.sqrt(n.doubleValue());
        for (int i = 2; i <= sqrt; i++) {
            if (na % i!= 0) continue;

            long ex = 0;
            while (na % i == 0) {
                ex++;
                na /= i;
            }
            pairList.add(new Pair(i, ex));
        }

        if (na!= 1) pairList.add(new Pair(na, 1));

        StringBuilder ans = new StringBuilder();
        for (Pair<Long, Long> p : pairList) {
            for (int i = 0; i < p.second; i++) {
                ans.append(" " + p.first);
            }
        }

        return n.toString() + ":" + ans.toString();
    }
}

