int main(){
  while(true){
    int res[N] = {0,};
    int hand[N] = {0,}; 

    for(int i = 0; i < N; ++i){
      cin >> hand[i];
      if( hand[i] == 0 ){
	return 0;
      }
    }

    for(int i = 0; i < N; ++i){
      bool bWin = false;
      bool bLose = false;
      for(int j = 0; j < N; ++j){
	if( i==j ){
	  continue;
	}
	switch( hand[i] ){
	case 1:
	  if( hand[j] == 2 ) bWin = true;
	  if( hand[j] == 3 ) bLose = true;
	  break;
	case 2:
	  if( hand[j] == 3 ) bWin = true;
	  if( hand[j] == 1 ) bLose = true;
	  break;
	case 3:
	  if( hand[j] == 1 ) bWin = true;
	  if( hand[j] == 2 ) bLose = true;
	  break;
	}
      }
      if( bWin && bLose ){
	res[i] = 3;
      }else if( bWin ){
	res[i] = 1;
      }else if( bLose ){
	res[i] = 2;
      }else{
	res[i] = 3;
      }
    }

    for(int i=0; i<N; ++i){
      cout << res[i] << '\n';
    }
  }
  return 0;
}

