struct UnionFind {
    var rank: [Int]
    var par:  [Int]
    var size: [Int]
    init(_ size: Int) {
        self.rank = IntArray(size) { 0 }
        self.par  = IntArray(size) { $0 }
        self.size = IntArray(size) { 1 }
    }
    mutating func unite(_ x: Int, _ y: Int) -> Bool {
        var rx = self.root(x)
        var ry = self.root(y)
        if (rx == ry) { return false }
        if (self.rank[rx] < self.rank[ry]) {
            let w = rx
            rx = ry
            ry = w
        } else if (self.rank[rx] == self.rank[ry]) {
            self.rank[rx] += 1
        }
        self.par[ry] = rx
        self.size[rx] += self.size[ry]
        return true
    }
    mutating func find(_ x: Int) -> Int {
        if (self.par[x] == x) { return x }
        else {
            self.par[x] = self.find(self.par[x])
            return self.par[x]
        }
    }
    mutating func isSame(_ x: Int, _ y: Int) -> Bool { self.root(x) == self.root(y) }
    mutating func size(_ x: Int) -> Int { self.size[self.find(x)] }
    mutating func root(_ x: Int) -> Int { self.find(x) }
}












