                dis[nx][ny] = dis[x][y] + c[nx][ny];
                extend(nx, ny);
            }
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                dis[i][j] = Math.min(Math.min(i, j), Math.min(n - i + 1, n - j + 1));
                c[i][j] = 1;
            }
        }
        int ans = 0;
        for (int i = 1; i <= n * n; i++) {
            int a = sc.nextInt();
            int x = (a - 1) / n + 1;
            int y = a % n;
            if (y == 0) y = n;
            ans += dis[x][y] - 1;
            c[x][y] = 0;
            for (int j = 0; j < 4; j++) {
                int nx = x + dx[j], ny = y + dy[j];
                dis[x][y] = Math.min(dis[x][y], dis[nx][ny]);
            }
            extend(x, y);
        }
        System.out.println(ans);
    }
}

