using System.Collections.Generic;
using System.Linq;
using System;

public class C
{
    public const int INF = 99999;
    public const int MINF = -99999;
}

public class Hello
{
    public static void Main()
    {
        while (true)
        {
            string[] line = Console.ReadLine().Trim().Split(' ');
            var n = int.Parse(line[0]);
            var m = int.Parse(line[1]);
            if (n == 0 && m == 0) break;
            var s = new int[n];
            for (int i = 0; i < m; i++)
            {
                var x = Console.ReadLine().Trim();
                if (x == "A") doA(s, n, 1);
                else if (x == "B") doB(s, n, 2);
                else if (x == "C") doC(s, n, 3);
                else doD(s, n, 4);
            }
            display(s);
        }

    }
    public static void display(int[] s)
    {
        foreach (var x in s)
            if (x == 1) Console.Write("A");
            else if (x == 2) Console.Write("B");
            else if (x == 3) Console.Write("C");
            else if (x == 4) Console.Write("D");
            else Console.Write("#");
        Console.WriteLine();
    }
    public static void doA(int[] s, int n, int A)
    {
        for (int i = 0; i < n; i++)
            if (s[i] == 0) { s[i] = A; break; }
    }
    public static void doB(int[] s, int n, int B)
    {
        if (n == 1) { s[0] = B; return; }
        if ( n-2 >= 0 &&  s[n - 1] == 0 && s[n - 2] != 1) { s[n - 1] = B; return; }
        for (int i = n - 2; i >= 1; i--)
            if (s[i] == 0 && s[i + 1] != 1 && s[i - 1] != 1) { s[i] = B; return; }
        if (s[0] == 0 && s[1] != 1) { s[0] = B; return; }
        doA(s, n, B);
    }
    public static void doC(int[] s, int n, int C)
    {
        if (n == 1) { s[0] = C;  return; }
        if (s.Sum() == 0)
        {
            if (n % 2 == 0) s[n / 2 ] = C;
            else s[(n + 1) / 2 -1] = C;
            return;
        }
        for (int i = 0; i < n; i++)
        {
            if (s[i] != 0 && (i + 1 < n) && s[i + 1] == 0) { s[i + 1] = C; return; }
            else if (s[i] != 0 && (i - 1 >= 0) && s[i - 1] == 0) { s[i - 1] = C; return; }
        }
    }
    public static void doD(int[] s, int n, int D)
    {
        if (s.Sum() == 0) { s[0] = D; return; }
        var abc = new List<int>();
        if (s[0] == 0) abc.Add(C.MINF);
        else abc.Add(0);
        for (int i = 1; i < n - 1; i++)
            if (s[i] != 0) abc.Add(i);
        if (s[n - 1] == 0) abc.Add(C.INF);
        else abc.Add(n - 1);
        var maxp = -1; var wL = 0; var maxL = -1;
        for (int i = 1; i < abc.Count(); i++)
        {
            var t = (abc[i] + abc[i - 1]) / 2;
            if (t < 0) { maxp = 0; maxL = abc[i] - 1; }
            else if (t > n - 1)
            {
                wL = n - 2 - abc[i - 1];
                if (wL > maxL) { maxL = wL; maxp = n - 1; }
            }
            else
            {
                if (t == i - 1) continue;
                wL = t - abc[i - 1] - 1;
                if (wL > maxL) { maxL = wL; maxp = t; }
            }
        }
        s[maxp] = D;
    }
}

