bool big(Syori a, Syori b, int i){
  if(a.data[syori[i].data[0]] == b.data[syori[i].data[0]]){
    if(a.data[syori[i].data[1]] == b.data[syori[i].data[1]]){
      if(a.data[syori[i].data[2]] == b.data[syori[i].data[2]]){
        return a.data[syori[i].data[3]] > b.data[syori[i].data[3]];
      }else{
