    while(j > 0){
      j = (j - 1) /2;
      if(big(segTree[i][j*2+1],segTree[i][j*2+2],i)){
        segTree[i][j] = segTree[i][j*2+1];
      }else{
        segTree[i][j] = segTree[i][j*2+2];
      }
    }
  }
}


void del(int x){
  int j;
