#include<bits/stdc++.h>
using namespace std;
#define MAX_N 3005
#define INF (1e9)

struct edge{int to,cost;};
typedef pair<int,int> P;

int N,R;
vector<edge> G[MAX_N];
int t[MAX_N][MAX_N];
set<int> ans;
bool visited[MAX_N];

void dfs(int pos,int d[MAX_N]){
  if(visited[pos])return;
  visited[pos]=true;
  ans.insert(pos);
  for(int i=0;i<(int)G[pos].size();i++){
    edge e=G[pos][i];
    if(d[e.to]+e.cost==d[pos])
      dfs(e.to,d);
  }
}

void dijkstra(int S,int d[MAX_N]){
  fill(d,d+N,INF);
  d[S]=0;
  priority_queue< P ,vector<P> ,greater<P> > Q;
  Q.push(P(0,S));
  while(!Q.empty()){
    P p=Q.top();Q.pop();
    int cost=p.first;
    int pos=p.second;
    if(cost>d[pos])continue;
    for(int i=0;i<(int)G[pos].size();i++){
      edge e=G[pos][i];
      if(d[e.to]>cost+e.cost){
        d[e.to]=cost+e.cost;
        Q.push(P(d[e.to],e.to));
      }
    }
  }
}

int main(){
  cin>>N>>R;
  for(int i=0;i<R;i++){
    int a,b,c;
    cin>>a>>b>>c;
    a--,b--;
    G[a].push_back((edge){b,c});
    G[b].push_back((edge){a,c});
  }

  for(int i=0;i<N;i++)
    dijkstra(i,t[i]);

  int maxm=0;
  for(int i=0;i<N;i++)
    for(int j=0;j<N;j++)
      maxm=max(maxm,t[i][j]);

  for(int i=0;i<N;i++){
    fill(visited,visited+N,false);
    for(int j=0;j<N;j++)
      if(t[i][j]==maxm)
        dfs(j,t[i]);
  }


  cout<<N-ans.size()<<endl;
  for(int i=0;i<N;i++)
    if(ans.count(i)==0)
      cout<<i+1<<endl;
  return 0;
}

