using System;

class quiz
{
    public int solve(int N, int[] L)
    {
        int[] Lprime = new int[N];
        int length;

        Lprime[0] = L[0];

        for (int i = 1; i < N; i++)
        {
            Lprime[i] = L[i] + Lprime[i - 1];
        }

        length = Lprime[N - 1];
        int[,] dp = new int[N + 1, length + 1];

        for (int i = 0; i < N + 1; i++)
        {
            for (int j = 0; j < length + 1; j++)
            {
                dp[i, j] = -1;
            }
        }

        for (int i = 0; i <= Lprime[0]; i++)
        {
            dp[1, i] = Lprime[0] - i;
        }

        for (int i = 1; i <= N; i++)
        {
            for (int j = 0; j <= Lprime[i - 1]; j++)
            {
                for (int k = 1; k < i; k++)
                {
                    if (dp[k, j] >= 0)
                    {
                        if (Lprime[i - 1] >= Lprime[k - 1] + j)
                        {
                            if (dp[i, j] >= 0)
                                dp[i, j] = Math.Min(dp[i, j], Math.Max(dp[k, j], Lprime[i - 1] - Lprime[k - 1] - j));
                            else
                                dp[i, j] = Math.Max(dp[k, j], Lprime[i - 1] - Lprime[k - 1] - j);
                        }
                        else if(i<N)
