def operate_u (tree1, tree2):
    length_tree1 = len(tree1)
    length_tree2 = len(tree2)

    if length_tree1 == 3 or length_tree2 == 3:
        if length_tree1 != 3:
            tree2 = tree1
            length_tree2 = length_tree1
        elif length_tree2 != 3:
            tree1 = tree2
            length_tree1 = length_tree2
        else:
