    elif e_result == "t1_bothandt2_right":
        return "(" + operate_u(tree1[1:center1_uo], tree1[1:center1_uo]) + "," + operate_u(tree1[center1_uo + 1:-1], tree2[2:-1]) + ")"
    else:
