fun main(args:Array<String>?): Unit {
    val prime = primes(123456 * 2)
    while(true){
        val n = readLine()!!.trim().toInt()
        if (n == 0) return
        println(upperBound(prime, n * 2) - upperBound(prime, n))
    }
}
fun primes(upper: Int): IntArray {
    val isPrime = BooleanArray(upper + 1){true}
    for (p in 2 until Math.sqrt(upper.toDouble()).toInt()){
        if (isPrime[p]) for (i in p * p .. upper step p) {
            isPrime[i] = false
        }
    }
    return (2 .. upper).filter(isPrime::get).toIntArray()
}
fun upperBound(array: IntArray, target: Int): Int {
    var left = 0
    var right = array.size
    while(left < right){
        val mid = (left + right) / 2
        if (array[mid] <= target) left = mid + 1
        else right = mid
    }
    return right
}

