public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[] prime = Arrays.stream(primes(123456 * 2));
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) return;
            System.out.println(upperBound(prime, n * 2) - upperBound(prime, n));
        }
    }

    public static int[] primes(int upper) {
        boolean[] isPrime = new boolean[upper + 1];
        for (int p = 2; p <= Math.sqrt(upper); p++) {
            if (isPrime[p]) for (int i = p * p; i <= upper; i += p) {
                isPrime[i] = false;
            }
        }
        return Arrays.stream(isPrime).mapToInt(i -> i * i)
               .toArray();
    }

    public static int upperBound(int[] array, int target) {
        int left = 0;
        int right = array.length;
        while (left < right) {
            int mid = (left + right) / 2;
            if (array[mid] <= target) left = mid + 1; else right = mid;
        }
        return right;
    }
}

