class Solver {
    StreamWriter sw;
    Scan sc;
    void Prt(string a) => sw.WriteLine(a);
    void Prt<T>(IEnumerable<T> a) => Prt(string.Join(" ", a));
    void Prt(params object[] a) => Prt(string.Join(" ", a));
    public Solver(StreamWriter sw) {
        this.sw = sw;
        this.sc = new Scan();
    }

    public void solve() {
        while (true) {
            int n, g, w, p;
            sc.Multi(out n, out g, out w, out p);
            if (n == 0) return;
            var a = new int[n];
            for (int i = 0; i < n; i++)
            {
                a[i] = g / 7 % 10;
                if (g % 2 == 0) g /= 2;
                else g = g / 2 ^ w;
            }
            Prt(solve(a, p));
        }
    }
    long solve(int[] s, long p) {
        long ans = 0;
        if (p == 2 || p == 5) {
            int c = 0;
            for (int i = 0; i < s.Length; i++) {
                if (s[i] != 0) ++c;
                if (s[i] % p == 0) ans += c;
            }
        }
        else {
            var q = new long[s.Length + 1];
            q[s.Length] = 0;
            long t = 1;
            var dic = new Dictionary<long, int>();
            dic.Add(0, 1);
            for (int i = s.Length - 1; i >= 0 ; i--)
            {
                t = t * 10 % p;
                q[i] = (t * s[i] + q[i + 1]) % p;
                if (s[i] != 0 && dic.ContainsKey(q[i])) ans += dic[q[i]];
                if (!dic.ContainsKey(q[i])) dic.Add(q[i], 0);
                ++dic[q[i]];
            }
        }
        return ans;
    }
}

