vector<Edge> G[SIZE];

//https://betrue12.hateblo.jp/entry/2020/08/09/150101

int main(){

	scanf("%d %d %d",&V,&E,&W);

	int abs_W = -W;

	for(int i = 0; i < V; i++){

		//dp[][] := true
		dp[i][0] = true;
		for(int k = 1; k <= abs_W-1; k++){
			dp[i][k] = false;
		}
	}
	for(int i = 0; i < V; i++){

		in_num[i] = 0;
	}

	int from,to,cost;

	for(int i = 0; i < E; i++){

		scanf("%d %d %d",&from,&to,&cost);
		G[from].push_back(Edge(to,cost));
		in_num[to]++;
	}

	queue<int> Q;
	for(int i = 0; i < V; i++){

		if(in_num[i] == 0){

			Q.push(i);
		}
	}

	int maximum = -BIG_NUM;

	while(!Q.empty()){

		int node_id = Q.front();
		Q.pop();

		for(int i = 0; i <= abs_W-1; i++){

			if(!dp[node_id][i])continue;

			for(int k = 0; k < G[node_id].size(); k++){

				int next_node = G[node_id][k].to;
				int next_cost = i+G[node_id][k].weight;

				if(next_cost >= abs_W)continue;

				maximum = max(maximum,next_cost+W);
				dp[next_node][next_cost] = true;
			}
		}

		for(int i = 0; i < G[node_id].size(); i++){

			int next = G[node_id][i].to;
			in_num[next]--;
			if(in_num[next] == 0){

				Q.push(next);
			}
		}
	}

	if(maximum == -BIG_NUM){

		printf("NA\n");
	}else{

		printf("%d\n",maximum);
	}


	return 0;
}

